#!/usr/bin/perl 

use warnings;
use strict;

use Data::Dumper;

#  Notes:
#  Ver 1.1 File, no checksums!
#  0x36 Component Data Header start(int), record size, count (0c05B4 position)
#  0x3E Feeder Data Header
#  Component Data length: 0x16
#  Feeder Data Lenght: 0x63

package JukiWriter;

sub new{
    my $class = shift;
    my $self = {};
    bless $self, $class;
    
    $self->{_fiducials}=[];
    
    return $self;
}

sub setBoardName{
  my($self)=shift;
  my($p)=shift;
  
  $self->{_boardName} = $p;
}

sub setFeeders{
  my($self)=shift;
  my($feeders)=shift;
  
  $self->{_feeders} = $feeders;

}

sub setFeederIndex{
  my($self)=shift;
  my($feeders)=shift;
  
  $self->{_feederIndex} = $feeders;

}

sub setPlacements{
  my($self)=shift;
  my($p)=shift;
  
  $self->{_placements} = $p;

}

sub setFiducials{
  my($self)=shift;
  my($p)=shift;
  
  $self->{_fiducials} = $p;

}

sub packPlacements{
    my($self) = shift;
    my($placements)=[];

    # 52 35 00 00 00   00  02 a0 86 01   00 80 a9 03   00  00 00    04   00 00 3b 00
    # [ string  5  ]       [ X pos   ]   [  Y pos  ]   [?] [rot  ]  [id] [????     ]
    
    # rotation is in 1/50ths of a degree
    # id is the component ID in the component list
    
    foreach(@{$self->{_placements}}){
      my($element)=$_;
      my($data)=pack("a5 x2 i< i< S< C C C C C",$element->{name},$element->{x}*10000,$element->{y}*10000,$element->{rotation}*50,$self->{_feederIndex}->{$element->{key}},0x00,0x00, 0x3B, 0x00);
      push(@{$placements},$data);
    }
   
    $self->{_placementData} = $placements;
}

  
sub packComponents{
    my($self) = shift;
    
    # 31 4B 30 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00  'a20
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00  'a20
    # 00 00 01 FF 00 00 'fixed data I think?
    # 01 00 '
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'x26
    # 90 08 00 40 'S<2
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'x21
    
    # 32 37 52 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 // value
    # 52 5F 30 38 30 35 00 00 00 00 00 00 00 00 00 00 00 00 00 00  // pattern
    # 00 00  
    # 02 FF  
    # 00 01  
    # 02 00 
    # E2 
    # 20 4E 00 00  // width
    # E0 2E 00 00  //depth
    # 88 13 00 00  // height
    # 3C F6 FF FF  // fixed 
    # 00 00 00 00 00 00 00 00 x8
    # 09 93 S<
    # 08 00 S<
    # DF 01 S<
    # 00 00 00  x3 
    # 07 00  S<
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 x15
    
    #     31 30 30 52 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'a20  value
    #     52 30 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'a20  pattern
    #     00 00 'S<
    #     02 FF 'S<
    #     0A 01 ??
    #     01 00 'S  count per board
    #     E2 
    #     20 4E 00 00 // width
    #     E0 2E 00 00 // depth
    #     88 13 00 00 // height
    #     3C F6 FF FF 00 00 00 00 00 00 00 00 09 93 08 00 DF 01 00 00 00 07 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00
    

    my($components)=[];
    
    foreach(@{$self->{_feeders}}){
      my($feeder)=$_;
      my($data)=pack("a20 x1 a20 x1 C2 S<2 x1 i< i< i< i< i< i< C2 S< S< x3 S< x15",
        $feeder->{name}." ".$feeder->{pattern},
        $feeder->{pattern},
        0x02,
        0xFF,
        0x0000,
        $feeder->{count},
        $feeder->{packageData}->{width}   * 10000, # component width
        $feeder->{packageData}->{length}  * 10000, # component depth
        $feeder->{packageData}->{height}  * 10000, # component height
        $feeder->{packageData}->{laser}   * 10000, # laser height (-0.25)
        $feeder->{packageData}->{offsetX} * 10000, # placement offset X
        $feeder->{packageData}->{offsetY} * 10000, # placement offset Y
        0x09,
        0x93,       # 93 is nozzle 2
        0x0004,     # 00 push 0, 04 push 1, 08 push 2, 0c push 3
        0x01DF,
        0x0007);
      push(@{$components},$data);
      #print length($data);
      #print "Length is ".$feeder->{packageData}->{length}."\n";
      #print "Width is ".$feeder->{packageData}->{width}."\n";
    }
    

    $self->{_feederData} = $components;

}

sub packData{
  my($self) = shift;
 
  $self->packPlacements();
  $self->packComponents();
 
}

sub writeFiducials{
  my($self)=shift;
  my($FW)=shift;
    
  if(scalar(@{$self->{_fiducials}}) > 0){
    my($count)=scalar(@{$self->{_fiducials}});
    print "we have $count fids\n";
  }
}
  
sub writeToFile{
  my($self)=shift;
  my($outfile)=shift;
  
  $self->packData();

    
  open(my$FW,">$outfile") or die "Can't open $outfile file for writing: $!\n";
  binmode($FW);
  
  #Copy over a V1.7 header block
  while (my $headerBlock = <DATA>) {
    my(@bytes) = split(' ',$headerBlock);
    foreach(@bytes){
      print $FW pack("H2",$_);
    }
  }
  
  # Copy over the title
  seek($FW,0,0);
  print $FW pack("a20",$self->{_boardName});
  
  #re-write the board title into to block as PWB ID
  seek($FW,0xE7,0);
  print $FW pack("a20",$self->{_boardName});
  
  #The placement data position will be OK, but the Component Data position needs adjusting.
  
  $self->writeFiducials($FW);
    
    my($placementOffset)=0x5B4;
    my($componentOffset) = $placementOffset + 0x16 * scalar(@{$self->{_placementData}});
    
    my($offset3) = $componentOffset + 0x63 * scalar(@{$self->{_feederData}});
    my($offset4) = $offset3 + +0x1430;
    my($offset5) = $offset4 + 0x23 * 0x32;
    my($offset6) = $offset5 + 0x13C0;
    my($offset7) = $offset5 + 0x64 * 0x32;
    
    # move to the place v1.1 likes us to stick the data
    seek($FW,$placementOffset,0);
    writeBytes($self->{_placementData},$FW,0x16);
    
    # move to the correct position and store the components
    seek($FW,$componentOffset,0);
    writeBytes($self->{_feederData},$FW,0x63);
    
    
    # Update the header with where we stored the placement data
    seek($FW,0x36,0);
    print $FW  pack("I<",0x05B4);
    print $FW  pack("S<",0x16);
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    
    # Update the header with where we are going to store the component data
    seek($FW,0x3E,0);
    print $FW  pack("I<",$componentOffset);
    print $FW  pack("S<",0x63);
    print $FW  pack("S<",scalar(@{$self->{_feederData}}));
    
    # drop in offset3
    seek($FW,0x46,0);
    print $FW  pack("I<",$offset3);
    
    # drop in offset4
    seek($FW,0x4E,0);
    print $FW  pack("I<",$offset4);
    
    # drop in offset5
    seek($FW,0x56,0);
    print $FW  pack("I<",$offset5);
    
    # drop in offset6
    seek($FW,0x5E,0);
    print $FW  pack("I<",$offset6);
    
    # drop in offset7
    seek($FW,0x66,0);
    print $FW  pack("I<",$offset7);
    
    
    #seek($FW,0xE7,0);
    #print $FW  pack("S<",0x05B4);
    #print $FW  pack("S<",$componentOffset);
    #print $FW  pack("S<",$offset3);
    #print $FW  pack("S<",$offset4);
    #print $FW  pack("S<",$offset5);
    #print $FW  pack("S<",$offset6);
    #print $FW  pack("S<",$offset7);
    
    # write some placements count and the component count into the file
    #8f seems to be the number of placement records used by the "data status" thing
    #90 seems to be the number of valid placement records used by the data status thing.
    
    seek($FW,0x8F,0);
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    
    seek($FW,0x9D,0);
    print $FW  pack("S<",scalar(@{$self->{_feederData}}));
    seek($FW,0x9F,0);
    #print $FW  pack("S<",scalar(@{$components}));
    
    seek($FW,0xB9,0);
    #print $FW  pack("S<",scalar(@{$components}));
    #print $FW  pack("S<",scalar(@{$components}));
    
    seek($FW,0x4278 + scalar(@{$self->{_placementData}})*0x1F +scalar(@{$self->{_feederData}})*0x73 ,0);
    print $FW  pack("S<",0x00);
    
    close ($FW);
}


# helper
sub writeBytes{
  my($bytes,$FW,$len)=@_;
  
  # print tell($FW)."\n";
  
  foreach(@{$bytes}){
     my($row)=$_;
     $row = substr($row,0,$len);
     print $FW $row;
  }
}

1;




