#!/usr/bin/perl 

use warnings;
use strict;

use Data::Dumper;


package JukiWriter;

sub new{
    my $class = shift;
    my $self = {};
    bless $self, $class;
    
 
    return $self;
}

sub setBoardName{
  my($self)=shift;
  my($p)=shift;
  
  $self->{_boardName} = $p;
}

sub setFeeders{
  my($self)=shift;
  my($feeders)=shift;
  
  $self->{_feeders} = $feeders;

}

sub setFeederIndex{
  my($self)=shift;
  my($feeders)=shift;
  
  $self->{_feederIndex} = $feeders;

}

sub setPlacements{
  my($self)=shift;
  my($p)=shift;
  
  $self->{_placements} = $p;

}

sub packData{
  my($self) = shift;
  
    my($placements)=[];
    my($components)=[];

    # 52 35 00 00 00   00  02   a0 86 01 00    80 a9 03 00   00 00    04   00 00 3b 00
    # [ string  5  ]           [ X pos   ]     [  Y pos  ]   [?]     [rot  ]  [id] [????     ]
    
    # rotation is in 1/50ths of a degree
    # id is the component ID in the component list
    
    foreach(@{$self->{_placements}}){
      my($element)=$_;
      my($data)=pack("a5 x C i< i< S< C C C C C",
          $element->{name},
          0x02,
          $element->{x}*10000,
          $element->{y}*10000,
          $element->{rotation}*50,
          $self->{_feederIndex}->{$element->{key}},
          0x00,
          0x00, 
          0x3B, 
          0x00
      );
      push(@{$placements},$data);
    }
    
    # 31 4B 30 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00  'a20
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00  'a20
    # 00 00 01 FF 00 00 'fixed data I think?
    # 01 00 '
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'x26
    # 90 08 00 40 'S<2
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'x21
    
    # 32 37 52 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 // value
    # 52 5F 30 38 30 35 00 00 00 00 00 00 00 00 00 00 00 00 00 00  // pattern
    # 00 00  
    # 02 FF  
    # 00 01  
    # 02 00 
    # E2 
    # 20 4E 00 00  // width
    # E0 2E 00 00  //depth
    # 88 13 00 00  // height
    # 3C F6 FF FF  // fixed 
    # 00 00 00 00 00 00 00 00 x8
    # 09 93 S<
    # 08 00 S<
    # DF 01 S<
    # 00 00 00  x3 
    # 07 00  S<
    # 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 x15
    
    #     31 30 30 52 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'a20  value
    #     52 30 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 'a20  pattern
    #     00 00 'S<
    #     02 FF 'S<
    #     0A 01 ??
    #     01 00 'S  count per board
    #     E2 
    #     20 4E 00 00 // width
    #     E0 2E 00 00 // depth
    #     88 13 00 00 // height
    #     3C F6 FF FF 00 00 00 00 00 00 00 00 09 93 08 00 DF 01 00 00 00 07 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00 00
    

    
    
    foreach(@{$self->{_feeders}}){
      my($feeder)=$_;
      my($data)=pack("a20 x1 a20 x1 S<3 C i< i< i< i< x8 S< S< S< x3 S< x15",
        $feeder->{name},
        $feeder->{pattern},
        0xFF02,0x00,$feeder->{count},0xE2,
        0x00004E20, #width
        0x00002EE0, #depth
        0x00001388, #height
        0xFFFFF63C, # fixed
        0x9309,
        0x0008,
        0x01DF,
        0x0007);
      push(@{$components},$data);
      #print "Width is ".$feeder->{packageData}->{width}."\n";
    }
    
    $self->{_placementData} = $placements;
    $self->{_feederData} = $components;

}

sub writeToFile{
  my($self)=shift;
  my($outfile)=shift;
  
  $self->packData();

    
  open(my$FW,">$outfile") or die "Can't open $outfile file for writing: $!\n";
  binmode($FW);
  
  #Copy over a V1.7 header block
  while (my $headerBlock = <DATA>) {
    my(@bytes) = split(' ',$headerBlock);
    foreach(@bytes){
      print $FW pack("H2",$_);
    }
  }
  
  # Copy over the title
  seek($FW,0,0);
  print $FW pack("a20",$self->{_boardName});
  
  #re-write the board title into to block
  seek($FW,0xE7,0);
  print $FW pack("a20",$self->{_boardName});
  #The placement data position will be OK, but the Component Data position needs adjusting.
    
    my($placementOffset)=0x05B4;
    my($componentOffset) = $placementOffset + 0x16 * scalar(@{$self->{_placementData}});
    
    my($offset3) = $componentOffset + 0x63 * scalar(@{$self->{_feederData}});
    my($offset4) = $offset3 + +0x1430;
    my($offset5) = $offset4 + 0x23 * 0x32;

    # Production data, length 38 at address 0x66
    my($offset7) = $offset5 + 0x64 * 0x32;
    # Production data position + 0x38 at address 0x5E
    my($offset6) = $offset7 + 0x38;
    
    # move to the place v1.7 likes us to stick the data
    seek($FW,$placementOffset,0);
    writeBytes($self->{_placementData},$FW,22);
    
    # move to the correct position and store the components
    seek($FW,$componentOffset,0);
    writeBytes($self->{_feederData},$FW,99);
    
    
    # Update the header with where we stored the placement data
    seek($FW,0x36,0);
    print $FW  pack("I<",$placementOffset);
    print $FW  pack("S<",22);
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    
    # Update the header with where we are going to store the component data
    seek($FW,0x3E,0);
    print $FW  pack("I<",$componentOffset);
    print $FW  pack("S<",99);
    print $FW  pack("S<",scalar(@{$self->{_feederData}}));
    
    # drop in offset3
    seek($FW,0x46,0);
    print $FW  pack("I<",$offset3);
    
    # drop in offset4
    seek($FW,0x4E,0);
    print $FW  pack("I<",$offset4);
    
    # drop in offset5
    seek($FW,0x56,0);
    print $FW  pack("I<",$offset5);
    
    # drop in offset6
    seek($FW,0x5E,0);
    print $FW  pack("I<",$offset6);
    
    # drop in offset for the production data
    seek($FW,0x66,0);
    print $FW  pack("I<",$offset7);
    
    
   #  writeProductionData($FW,$offset7,scalar(@{$self->{_placementData}}));
    
    # write some placements count and the component count into the file
    #8f seems to be the number of placement records used by the "data status" thing
    #90 seems to be the number of valid placement records used by the data status thing.
    
    seek($FW,0x8F,0);
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    
    seek($FW,0x9D,0);
    print $FW  pack("S<",scalar(@{$self->{_feederData}}));
    seek($FW,0x9F,0);
    #print $FW  pack("S<",scalar(@{$components}));
    
    # Data does not load at all when these set
    seek($FW,0xB9,0);
    #print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    #print $FW  pack("S<",scalar(@{$self->{_placementData}}));
    
    
    close ($FW);
}

sub writeProductionData{

    my($FW,$location,$count)=@_;
    
    # write production data 
    
    seek($FW,$location,0);
    # Offset 0x00 X placement offset default 0, but eg signed C7 = 199 = 0.02mm
    # 00 00 00 00
    print $FW  pack("I<",0xC7);
    
    # Offset 0x04 X placement offset signed C7 = 199 = 0.02mm
    # 00 00 00 00 
    print $FW  pack("I<",0x041F);
    
    # Place, All, ADH,  ALL=00, ADH=02, PLACE=01  + 0x04 for "step" mode instead of continous
    # 00 00 
    print $FW  pack("S<",0x00);
    
    # Number planned offset 0x0A  (default 1) 
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # Number completed  offset 0x0C (default 0)
    # 00 00 
    print $FW  pack("S<",0x00);
        
    # Start at offset 0x0E (default 1)
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # Stop At offset 0x10  (default to number of placements)
    # 03 00 
    print $FW  pack("S<",$count);
    
    # Offset 0x12  Starting position circuit (default 1)
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # Offset 0x14 Starting position step number (default 1)
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # Fuck knows what this lot is, probably trial run data?
    # 00 00
    print $FW  pack("S<",0x00);
    
    # 00 00 
    print $FW  pack("S<",0x00);
    
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # 00 00 00 00 00 
    print $FW  pack("I<",0x00000000);
    
    # 00 00 00 00 00
    print $FW  pack("I<",0x00000000);
    
    # 00 00 
    print $FW  pack("S<",0x00);
    
    # delay trial run x 10 ms
    # 0A 00 
    print $FW  pack("S<",0x0A);
    
    # ???
    # 00 00
    print $FW  pack("S<",0x00);
    
    # dry run data?
    
    # planned dry run
    # 01 00
    print $FW  pack("S<",0x01);
    
    # complete dry run
    # 00 00
    print $FW  pack("S<",0x00);
    
    # start at
    # 01 00 
    print $FW  pack("S<",0x01);
    
    # stop at  offset 0x30  default placement count
    # 03 00 
    print $FW  pack("S<",$count);
    
    # ????
    # 00 00 00 00
    print $FW  pack("I<",0x00000000);
    
    # delay x 10 ms
    # 0A 00
    print $FW  pack("S<",0x0A);
}


# helper
sub writeBytes{
  my($bytes,$FW,$len)=@_;
  
  # print tell($FW)."\n";
  
  foreach(@{$bytes}){
     my($row)=$_;
     $row = substr($row,0,$len);
     print $FW $row;
  }
}

1;




